import styled from '@emotion/styled';

const Container = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  background: #343541;
`;

const ChatContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
`;

const InputContainer = styled.div`
  padding: 20px;
  border-top: 1px solid #444654;
`;

const MessageInput = styled.input`
  width: 100%;
  padding: 12px;
  border-radius: 5px;
  border: 1px solid #565869;
  background: #40414f;
  color: white;
  font-size: 16px;
  &:focus {
    outline: none;
    border-color: #565869;
  }
`;

const Message = styled.div`
  margin: 20px 0;
  padding: 15px;
  border-radius: 5px;
  color: white;
  background: ${props => props.isUser ? '#444654' : '#343541'};
`;

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Add user message
    const newMessages = [...messages, { text: input, isUser: true }];
    setMessages(newMessages);

    // Simulate AI response
    setTimeout(() => {
      setMessages([...newMessages, { 
        text: "I am a chatbot simulation. I can respond to your messages but I don't have real AI capabilities.", 
        isUser: false 
      }]);
    }, 1000);

    setInput('');
  };

  return (
    <Container>
      <ChatContainer>
        {messages.map((message, index) => (
          <Message key={index} isUser={message.isUser}>
            {message.text}
          </Message>
        ))}
      </ChatContainer>
      <InputContainer>
        <form onSubmit={handleSubmit}>
          <MessageInput
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type a message..."
          />
        </form>
      </InputContainer>
    </Container>
  );
}

export default App;

